function mobileNav(){$(".mobile-nav-toggle").on("click",function(){var o=$(this).hasClass("is-open");o?$(".mobile-nav-toggle, .mobile-nav").removeClass("is-open"):$(".mobile-nav-toggle, .mobile-nav").addClass("is-open")})}$(window).scroll(function(){var o=$(this).scrollTop();$(".head-img").css({"background-position":"100% "+o/2.75+"%"})}),$(function(){mobileNav()});