function mobileNav(){$(".mobile-nav-toggle").on("click",function(){var o=$(this).hasClass("is-open");o?$(".mobile-nav-toggle, .mobile-nav").removeClass("is-open"):$(".mobile-nav-toggle, .mobile-nav").addClass("is-open")})}function headerImageScroll(){var o=$(window).scrollTop();$("section.head-img").css("background-position","right  -"+o+"px")}$(function(){mobileNav()}),$(window).scroll(function(){headerImageScroll()});