function mobileNav(){$(".mobile-nav-toggle").on("click",function(){var o=$(this).hasClass("is-open");o?$(".mobile-nav-toggle, .mobile-nav").removeClass("is-open"):$(".mobile-nav-toggle, .mobile-nav").addClass("is-open")})}$(window).scroll(function(){var o=$(this).scrollTop();$(".col-title").css({transform:"translate(0, "+o/6+"%)"})}),$(function(){mobileNav()});